     <script type="text/javascript">
         //进行验证的代码
           $(function(){
                function changeImg(){
                     $("#randomCodeImg").attr('src',function(i,v){
                         
                          return v+'?'+Math.random();
                      
                      });
                
                }
              
               $("#randomCodeImg_do").click(function(){
                      //验证码的更换
                      changeImg();
                });
                      
                      //标志位
                      var mark=0;
                      var user,pwd,code;
                     //用户名处理
                     
                     
                    
                     $("#username").blur(function(){
                           
                             var user=$(this).val();
                             
                             if(user.length==0||user==""||!(/^(\d|[a-z]){6,20}$/i.test(user))||/^\d{6,20}$/i.test(user)){
                                 $("#msg_email").html('<span class="vam icon-error">对不起，您输入的用户名不符合格式</span>');
                             }else if((/^(\d|[a-z]){6,20}$/.test(user))){
                                  //进行ajax查询 用户是否存在
                                  //<span class="vam icon-ok"> </span>
                                  $.ajax({
                                       url:"/vmallshop/index.php/Home/Sign/checkName.html",
                                       type:"POST",
                                       data:{ 
                                              'name':user,
                                       
                                       },
                                       success:function(responseText,status,xhr){
                                          if(status=="success"){
                                               // 0不和发  1在数据库有 2合法
                                              
                                              if(responseText==0){
                                                  $("#msg_email").html('<span class="vam icon-error">对不起，您输入的用户名不符合格式</span>');
                                              
                                               }else if(responseText==1){
                                                   $("#msg_email").html('<span class="vam icon-error">用户已被注册</span>');
                                              }else if(responseText==2){
                                                   //如果合法就把mark标志 改为1
                                                  $("#msg_email").html('<span class="vam icon-ok"> </span>');
                                                   mark=1;
                                              }
                                          }else{
                                              $("#msg_email").html('<span class="vam icon-error"></span>');
                                          }
                                       
                                       },
                                       
                                       
                                       timeout:1000*60,
                                  
                                  
                                  
                                  });
                                  
                             
                             
                             
                             }else{
                               $("#msg_email").html('<span class="vam icon-error">对不起，您输入的用户名不符合格式</span>');
                             
                             }
                          
                           
                     
                     
                     });
                     
                     $("#username").focus(function(){
                         $("#msg_email").html('');
                     
                     });
                     
                     //密码验证
                     
                     $("#input_label_0").focus(function(){
                          $("#msg_password").html();
                     });
                     
                     $("#input_label_0").blur(function(){
                         //获取值进行判断
                         var pwd=$("#input_label_0").val();
                        
                         if(/^(\d|[a-z]){6,32}$/i.test(pwd)){
                             $("#msg_password").html('<span class="vam icon-ok"> </span>');
                             mark=1;
                         
                         }else{
                             $("#msg_password").html('<span class="vam icon-error">不符合规定</span>');
                             mark=0;
                         }
                         
                     
                     });
                        //判断密码强度 6-10 弱  10-20 中  20-30强
                     $("#input_label_0").keyup(function(){
                          var pwd=$("#input_label_0").val();
                          
                          var pwd_letter=$(".pwd-letter span");
                          
                          var i=0;
                          if(pwd.length>0){
                              if(pwd.length<=10||(/^\d{6,32}$/i.test(pwd))||(/^[a-z]{6,32}$/i.test(pwd))){
                                  i=0;
                              }else if(pwd.length>10&&pwd.length<=20){
                                  i=1;
                              }else if(pwd.length>20&&pwd.length<=32){
                                  i=2;
                              }
                             
                              pwd_letter.eq(i).addClass("power").siblings().removeClass("power");;
                          }else{
                              pwd_letter.removeClass('power');
                          
                          }
                          
                     
                     
                     });
                     
                     //确认密码处理
                     $("#input_label_1").focus(function(){
                         $("#msg_checkPassword").html('');
                     
                     });
                     $("#input_label_1").blur(function(){
                         var  pwd=$("#input_label_0").val();
                         
                         var repwd=$("#input_label_1").val();

                          if((repwd==pwd)&&(/^(\d|[a-z]){6,32}$/.test(repwd))){
                             mark=1;
                             $("#msg_checkPassword").html('<span class="vam icon-ok"> </span>');
                          }else{
                             mark=0;
                             $("#msg_checkPassword").html('<span class="vam icon-error"></span>');
                          }                  
                            
                         
                     
                     
                     
                     
                     });
                     
                     //验证码处理
                     //msg_randomCode
                      $("#randomCode").focus(function(){
                          $("#msg_randomCode").html('');
                      });
                     $("#randomCode").blur(function(){
                         var code_value=$(this).val();
                         if(code_value.length<4||!(/^(\d|[a-z]){4}$/i.test(code_value))){
                              $("#msg_randomCode").html('<span class="vam icon-error">不合法或位数不够</span>');
                               changeImg();
                              mark=0;
                         }else{
                              //ajax去验证
                              $.ajax({
                                  url:"/vmallshop/index.php/Home/Sign/check_verify.html",
                                  type:"GET",
                                  data:{
                                     'code':code_value,
                                  },
                                  success:function(responseText,status,xhr){
                                     if(status=='success'){
                                        if(responseText==1){
                                           mark=1;
                                           $("#msg_randomCode").html('<span class="vam icon-ok"> </span>');
                                        }else{
                                            mark=0;
                                            $("#msg_randomCode").html('<span class="vam icon-error">不正确</span>');
                                             changeImg();
                                        }
                                     }else{
                                           mark=0;
                                           $("#msg_randomCode").html('<span class="vam icon-error">不正确</span>');
                                            changeImg();
                                     }
                                  
                                  },
                                  
                                  error:function(){
                                     $("#msg_randomCode").html('<span class="vam icon-error"></span>');
                                      changeImg();
                                  },
                                  
                                  timeout:1000*60,
                                  
                              
                              });
                         }
                     
                     
                     });
                     
                     //单击登录按钮进行处理
                     
                     $("#btnSubmit").click(function(){
                         var user=$("#username").val();
                         var pwd=$("#input_label_0").val();
                         var repwd=$("#input_label_1").val();
                         var code_value=$("#randomCode").val();
                          //先判断是否有没有输入的
                         if(user.length==0){
                             mark=0;
                             $("#msg_email").html('<span class="vam icon-error">用户名不能为空</span>');
                         }
                         if(pwd.length==0){
                             mark=0;
                             $("#msg_password").html('<span class="vam icon-error">密码不能为空</span>');
                         
                         }
                          
                         if(repwd.length==0){
                             mark==0;
                             $("#msg_checkPassword").html('<span class="vam icon-error">确认密码不能为空</span>');
                         }
                         
                         if(code_value.length==0){
                              mark=0;
                             $("#msg_randomCode").html('<span class="vam icon-error">验证码不能为空</span>');
                              changeImg();
                         
                         }
                         
                         if(mark==1){
                             //成功开始注册
                             var url="/vmallshop/index.php/Home/Index/index.html";
                             $.ajax({
                                  url:"/vmallshop/index.php/Home/Sign/loginDo.html",
                                  type:"POST",
                                  data:{
                                     'name':user,
                                     'pwd':pwd,
                                  },
                                  success:function(responseText,status,xhr){
                                     if(status=="success"){
                                        if(responseText==1){
                                          $("#register_msg").html('<span class="vam icon-ok">注册成功!</span>');
                                           window.location.href=url;
                                        
                                        }else{
                                           $("#register_msg").html('<span class="vam icon-error">注册失败</span>');
                                            changeImg();
                                        }
                                     
                                     }else{
                                       $("#register_msg").html('<span class="vam icon-error">注册失败</span>');
                                        changeImg();
                                     
                                     }
                                  }
                             
                             });
                            
                         }else{
                            $("#register_msg").html('<span class="vam icon-error">注册失败</span>');
                             changeImg();
                          
                         }
                     
                     
                     });
             
               
               
               
           
           
           
           
           
           
           
           
           
           
           
           
           
           
           
           
           
           
           
           
           
           
           
           });
         
     
     
     </script>
